from rest_framework import serializersfrom .models import School, ClassGroup, Student, Test, Question, Resultclass SchoolSerializer(serializers.ModelSerializer):    class Meta:        model = School        fields = '__all__'class ClassGroupSerializer(serializers.ModelSerializer):    class Meta:        model = ClassGroup        fields = '__all__'class StudentSerializer(serializers.ModelSerializer):    class Meta:        model = Student        fields = '__all__'class TestSerializer(serializers.ModelSerializer):    class Meta:        model = Test        fields = ['id', 'name', 'class_group', 'max_score', 'duration', 'start_time']class QuestionSerializer(serializers.ModelSerializer):    class Meta:        model = Question        fields = '__all__'class ResultSerializer(serializers.ModelSerializer):    class Meta:        model = Result        fields = '__all__'